      {/* <img src={avatar} alt="" width="60" height="60" /> */}
      {/* <p>{user}</p>
      <p>{tweets} tweets</p> */}
      <p> {+finalFollowers} followers</p>
      <FollowBtn onClick={hadleclick} />
    </Wrapper>
  );
};
