  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers().then((data) => setUsers(data));
  }, []);
  // console.log(users);
  console.log(users);

  switch (filter) {
    case "all":
      return users;
    case "follow":
      return users.filter(({ id }) => !myFollowings.includes(id));
    case "followings":
      return users.filter(({ id }) => myFollowings.includes(id));

    default:
      return users;
  }
  // const filteredUsers = users.filter(user=> user)

  return (
    <ul>
      {users.map((user) => (
        <li key={user.id}>
