import Select from "react-select";

const options = [
  { value: "all", label: "All", name: "all" },
  { value: "follow", label: "Follow", name: "follow" },
  { value: "followings", label: "Followings", name: "followings" },
];

export const Filter = () => {
  let fltrRef = useRef();
  console.log(fltrRef.current.props.value.name);

  useEffect(() => {
    // Эффект выполняется после монтирования,
    // поэтому всегда будет ссылкой на DOM-элемент
    console.log(fltrRef.current.props.value.name);
  });

  const handleClick = () => {
    // Клики будут после монтирвоания,
    // поэтому всегда будет ссылкой на DOM-элемент
    console.log(fltrRef.current.props.value.name);
  };
  console.log(fltrRef.current.props.value.name);
  return (
    <Select
      options={options}
      ref={fltrRef}
      onChange={handleClick}
      defaultInputValue="All"
    />
  );
};
