import Select from "react-select";

const filterValues = Object({
  all: { value: "show all", label: "Show all" },
  follow: { value: "follow", label: "Follow" },
  followings: { value: "followings", label: "Followings" },
});

const options = [
  filterValues.all,
  filterValues.follow,
  filterValues.followings,
];

export const Filter = ({ selectedOption, setSelectedOption }) => {
  return (
    <Select
      name="selected-option"
      value={selectedOption}
      onChange={setSelectedOption}
      options={options}
    />
  );
};

const { all, follow, followings } = filterValues;

export const filterUsers = (users, myFollowings, filter) => {
  switch (filter) {
    case all.value:
      return users;
    case follow.value:
      return users.filter(({ id }) => !myFollowings.includes(id));
    case followings.value:
      return users.filter(({ id }) => myFollowings.includes(id));
    default:
      return users;
  }
};
