const options = [
  { value: "all", label: "All" },
  { value: "follow", label: "Follow" },
  { value: "followings", label: "Followings" },
];

export const Filter = () => {
  const handleChange = (e) => {
    console.log(e.value);
  };

  return (
    <Select
      options={options}
      onChange={handleChange}
      defaultValue={options[0]}
    />
  );
};
